import React, { Component } from "react";
import { Card, Col, Row } from "react-bootstrap";
import { BrowserRouter, Route } from "react-router-dom";
// import { Axios } from "axios";
import { API_URL } from "./utils/constants";
import { Home, Hasil, Listcategories, NavbarComponent } from "./components";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      menus: [],
    };
    console.log("menjalankan constructor");
  }

  componentDidMount() {
    const axios = require("axios");

    // Make a request for a user with a given ID
    axios
      .get(API_URL + "products")
      .then((res) => {
        console.log("response", res);
        const menus = res.data;
        this.setState([menus]);
      })
      .catch((error) => {
        // handle error
        console.log(error);
      })
      .then(function () {
        // always executed
      });
  }
  render() {
    const { menus } = this.state;
    return (
      <div>
        <BrowserRouter>
          <NavbarComponent />
          <div class="container-fluid">
            <Card className="p-3 my-4 border-0">
              <Row>
                <Listcategories />
                <Col md={7} mt="2">
                  <h6>
                    <strong>Daftar Produk</strong>
                  </h6>
                  <hr />
                  <Row>
                    {menus &&
                      menus.map((menu) => (
                        <>
                          <h3>{menu.nama}</h3>
                          <br />
                        </>
                      ))}
                  </Row>
                </Col>
                <Hasil />
              </Row>
            </Card>
          </div>
          <div class="container">
            <div className="container bg-secondary text-white p-3 mt-4">
              <Route exact path="/" component={Home} />
            </div>
          </div>
        </BrowserRouter>
      </div>
    );
  }
}

export default App;
